mapper.select:
SELECT COUNT(1)
FROM shop_expense
WHERE is_deleted = 0

    AND category = #{category}


    AND description LIKE CONCAT('%', #{keyword}, '%')


    AND spend_date >= #{startDate}


    AND spend_date <= #{endDate}


    AND amount >= #{minAmount}


    AND amount <= #{maxAmount}

SELECT COUNT(1)
FROM shop_expense
WHERE is_deleted = 1

    AND category = #{category}


    AND description LIKE CONCAT('%', #{keyword}, '%')


    AND spend_date >= #{startDate}


    AND spend_date <= #{endDate}


    AND amount >= #{minAmount}


    AND amount <= #{maxAmount}

SELECT
id,
category,
amount,
spend_date AS spendDate,
description,
is_deleted AS isDeleted,
create_time AS createTime,
update_time AS updateTime
FROM shop_expense
WHERE is_deleted = 0

    AND description LIKE CONCAT('%', #{keyword}, '%')


    AND category = #{category}


    AND spend_date >= #{startDate}


    AND spend_date <= #{endDate}


    AND amount >= #{minAmount}


    AND amount <= #{maxAmount}

ORDER BY

    
        ${sortBy}
    
    
        spend_date
    


    
        ${order}
    
    
        DESC
    

LIMIT #{size} OFFSET #{offset}

SELECT
id,
category,
amount,
spend_date AS spendDate,
description,
