mapper.select:
SELECT *
FROM member

  
    AND (name LIKE CONCAT('%', #{keyword}, '%') OR phone LIKE CONCAT('%', #{keyword}, '%'))
  
  
    AND balance >= #{minBalance}
  
  
    AND balance <= #{maxBalance}
  
  
    AND created_at >= #{startDate}
  
  
    AND created_at <= #{endDate}
  

ORDER BY
  ${sortBy} ${order}
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM member

  
    AND (name LIKE CONCAT('%', #{keyword}, '%') OR phone LIKE CONCAT('%', #{keyword}, '%'))
  
  
    AND balance >= #{minBalance}
  
  
    AND balance <= #{maxBalance}
  
  
    AND created_at >= #{startDate}
  
  
    AND created_at <= #{endDate}

SELECT *
FROM member
WHERE is_deleted = 1

    AND (name LIKE CONCAT('%', #{keyword}, '%') OR phone LIKE CONCAT('%', #{keyword}, '%'))


    AND balance >= #{minBalance}


    AND balance <= #{maxBalance}


    AND created_time >= #{startDate}


    AND created_time <= #{endDate}

ORDER BY
    
        
            ${sortBy}
        
        
            name
        
    
    
        
            DESC
        
        
            ASC
        
    
LIMIT #{size} OFFSET #{offset}

