mapper.insert:
INSERT INTO operation_log (
    operation,
    module,
    detail
) VALUES (
             #{operation},
             #{module},
             #{detail}
         )

mapper.select:
SELECT COUNT(*) FROM operation_log

    
        AND operation = #{operation}
    
    
        AND module = #{module}
    
    
        AND operation_time >= #{startTime}
    
    
        AND operation_time <= #{endTime}

SELECT id, module, operation, detail, operation_time
FROM operation_log

    
        AND operation = #{operation}
    
    
        AND module = #{module}
    
    
        AND operation_time >= #{startTime}
    
    
        AND operation_time <= #{endTime}
    


    
        ORDER BY ${sortBy}
    
    
        ORDER BY operation_time
    


    
        ASC
    
    
        DESC
    

LIMIT #{offset}, #{size}
