mapper.select:
SELECT COUNT(*)
FROM staff
WHERE is_deleted = 0

    AND (
    name LIKE CONCAT('%', #{keyword}, '%')
    OR phone LIKE CONCAT('%', #{keyword}, '%')
    )


    AND commission >= #{commissionMin}


    AND commission <= #{commissionMax}

SELECT COUNT(*)
FROM staff
WHERE is_deleted = 1

    AND (
    name LIKE CONCAT('%', #{keyword}, '%')
    OR phone LIKE CONCAT('%', #{keyword}, '%')
    )


    AND commission >= #{commissionMin}


    AND commission <= #{commissionMax}

SELECT id, name, phone, commission, is_deleted, create_time, update_time FROM staff WHERE is_deleted = 0
AND ( name LIKE CONCAT('%', #{keyword}, '%') OR phone LIKE CONCAT('%', #{keyword}, '%') )
AND commission >= #{commissionMin}
AND commission <= #{commissionMax}
ORDER BY CONVERT(name USING gbk)
ORDER BY commission
ORDER BY CONVERT(name USING gbk)
${order} LIMIT #{pageSize} OFFSET #{offset}

SELECT id, name, phone, commission, is_deleted, create_time, update_time FROM staff WHERE is_deleted = 1
AND ( name LIKE CONCAT('%', #{keyword}, '%') OR phone LIKE CONCAT('%', #{keyword}, '%') )
AND commission >= #{commissionMin}
AND commission <= #{commissionMax}
ORDER BY CONVERT(name USING gbk)
ORDER BY commission
ORDER BY CONVERT(name USING gbk)
${order} LIMIT #{pageSize} OFFSET #{offset}

SELECT *
FROM staff
WHERE name = #{name}
  AND phone = #{phone}
  AND is_deleted = 1
